function setLanguage(a) {
    localStorage.setItem("language", a), (language = localStorage.getItem("language")), getLanguage();
}
function getLanguage() {
    null == language && setLanguage(default_lang),
        $.ajax({ url: "locales/" + language + ".json", dataType: "json", async: !0 }).done(function (a) {
            $("body").attr("class", language),
                $.each(a, function (a, e) {
                    "head" === a && $(document).attr("title", e.title),
                        $(a)
                            .children()
                            .each(function () {
                                $(this).text(e[$(this).attr("key")]);
                            });
                });
        });
}
var userLang = navigator.language || navigator.userLanguage;

var language = localStorage.getItem("language"),
    default_lang = "en";

$(document).ready(function () {
    null != language && language !== default_lang && getLanguage(language);
    console.log(userLang);
    if (userLang === "tr-TR") {
        default_lang = "tr";
    } else {
        default_lang = "en";
    }
});

$(function () {
    if (language === "en") {
        $(".tr_lan").removeClass("active-flag");
        $(".en_lang").addClass("active-flag");
    }
    if (language === "tr") {
        $(".en_lang").removeClass("active-flag");
        $(".tr_lang").addClass("active-flag");
    }
    // French button
    $(".tr_lang").click(function () {
        // Active or remove the opacity on the flags.
        $(".tr_lang").addClass("active-flag");
        $(".en_lang").removeClass("active-flag");
    });

    // English button
    $(".en_lang").click(function () {
        // Active or remove the opacity on the flags.
        $(".en_lang").addClass("active-flag");
        $(".tr_lang").removeClass("active-flag");
    });
});
